import logo from './logo.svg';
import './App.css';

function App() {
  // define a function to add buttons to the page
  const addButtons = () => {
    // create an array of button texts
    const buttonTexts = ['Easy', 'Normal', 'Hard'];

    // loop through the array and create a button element for each text
    buttonTexts.forEach(text => {
      const button = document.createElement('button');
      button.innerText = text;
      button.classList.add('button-34');
      document.body.appendChild(button);
    });

    // remove the original button container element
    const buttonContainer = document.getElementById('button-container');
    document.body.removeChild(buttonContainer);
  };

  return (
    <div className="App">
      <header className="App-header">
        <img src={logo} className="App-logo" alt="logo" />
        <div>
          <p>Let's play a small game, choose a difficulty</p>
        </div>
        <div id="button-container">
          <button onClick={addButtons}>Select Difficulty</button>
        </div>
      </header>
    </div>
  );
}

export default App;
