import logo from './logo.svg';
import './App.css';
import axios from 'axios';

function App() {

  const difficulties = [
    { name: 'Easy', value: 3 },
    { name: 'Normal', value: 4 },
    { name: 'Hard', value: 5 },
    { name: 'God Mode', value: 6}
  ];

  const handleDifficultyClick = (value) => {
    console.log(`Selected difficulty: ${value}`);
    axios.get('http://127.0.0.1:8000/games/random/reviews/?difficulty=' + value)
    .then(res => res.json())
    .then(data =>{
      console.log(data)
      const game_list = JSON.parse(data)
      console.log(game_list)
    })
  };

  return (
    <div className="App">
      <header className="App-header">
        <img src={logo} className="App-logo" alt="logo" />
        <div>
          <p>
            Let's play a small game, choose a difficulty
          </p>
        </div>
        <div className='difficulty_selector'>
          {difficulties.map((difficulty) => (
            <button
              key={difficulty.value}
              type="button"
              className='button-34'
              onClick={() => handleDifficultyClick(difficulty.value)}
            >
              {difficulty.name}
            </button>
          ))}
        </div>
      </header>
    </div>
  );
}

export default App;
