import logo from '../logo.svg';
import '../App.css';
import axios from 'axios';

const difficulties = [
    { name: 'Easy', value: 3 },
    { name: 'Normal', value: 4 },
    { name: 'Hard', value: 5 },
    { name: 'God Mode', value: 6}
  ];



  const handleDifficultyClick = (value) => {
    console.log(`Selected difficulty: ${value}`);
    document.getElementById('diff_sel').remove();
    document.getElementById('warning').innerHTML = 'Loading your Impending Doom'
    const gameImageDiv = document.createElement('div');

    axios.get('http://127.0.0.1:8000/games/random/reviews/?difficulty=' + value)
      .then(res => {
        console.log(res.data); // accessing the data directly
        const game_list = JSON.parse(res.data);
        document.getElementById('warning').innerHTML =  'task done'
        for (let element of game_list){
          const imgElement = document.createElement('img');
          imgElement.src = element['header_image'];
          imgElement.className = 'App-logo';
          imgElement.alt = 'logo';
          gameImageDiv.appendChild(imgElement);
        }
        console.log(gameImageDiv);
        document.getElementById('App').appendChild(gameImageDiv); // appending gameImageDiv to App
      });
  };
function home(){

  return (
    <div className="App">
      <header className="App-header">
        <img src={logo} className="App-logo" alt="logo" />
        <div>
          <p id='warning'>
            Let's play a small game, choose a difficulty
          </p>
        </div>
        <div className='difficulty_selector' id='diff_sel'>

          {difficulties.map((difficulty) => (
            <button
              key={difficulty.value}
              type="button"
              className='button-34'
              onClick={() => handleDifficultyClick(difficulty.value)}
            >
              {difficulty.name}
            </button>
          ))}
        </div>
      </header>
    </div>
  );
          }

export default home
