import logo from './logo.svg';
import './App.css';
import {useRef, useEffect} from 'react';

function App() {
  const ref = useRef(null);

  useEffect(() => {
    const handleClick = event => {
      console.log('Button clicked');

      console.log('bobbyhadz.com');
    };

    const element = ref.current;

    element.addEventListener('click', handleClick);

    return () => {
      element.removeEventListener('click', handleClick);
    };
  }, []);
  return (
    <div className="App">
      <header className="App-header">
        <img src={logo} className="App-logo" alt="logo" />
        <div>
        <p>
          Let's play a small game, choose a difficulty
        </p>
        </div>
        <div class='difficulty_selector'>
        <button type="button" class='button-34'>Easy</button>
        <button type="button" class='button-34'>Normal</button>
        <button type="button" class='button-34'>Hard</button>
        </div>
      </header>
    </div>
  );
}

export default App;
